import { mode, getColorScheme } from '../tools';
import { Platform } from 'react-native';

const baseStyle = props => {
  const colorScheme = getColorScheme(props); //TODO: Use of Platform can be removed

  return {
    _disabled: {
      opacity: 0.4
    },
    _invalid: {
      borderWidth: 1,
      borderRadius: 16,
      borderColor: 'danger.600'
    },
    offTrackColor: mode(Platform.OS !== 'ios' ? 'gray.400' : 'gray.200', Platform.OS !== 'ios' ? 'gray.700' : 'gray.600')(props),
    onTrackColor: mode(Platform.OS !== 'ios' ? "".concat(colorScheme, ".300") : "".concat(colorScheme, ".500"), Platform.OS !== 'ios' ? "".concat(colorScheme, ".700") : "".concat(colorScheme, ".500"))(props),
    onThumbColor: mode(Platform.OS !== 'ios' ? "".concat(colorScheme, ".600") : 'white', Platform.OS !== 'ios' ? "".concat(colorScheme, ".500") : 'white')(props),
    offThumbColor: mode(Platform.OS !== 'ios' ? 'gray.100' : 'white', Platform.OS !== 'ios' ? 'gray.200' : 'white')(props)
  };
};

const sizes = {
  sm: {
    style: {
      transform: [{
        scale: 0.75
      }]
    }
  },
  md: {},
  lg: {
    style: {
      transform: [{
        scale: 1.25
      }]
    },
    margin: 1
  }
};
const defaultProps = {
  size: 'md',
  colorScheme: 'primary'
};
export default {
  baseStyle,
  sizes,
  defaultProps
};
