import { mode } from './../tools';

function baseStyle(props) {
  return {
    borderRadius: 'full',
    borderWidth: 2,
    borderColor: mode('light.50', 'gray.800')(props),
    bg: mode('gray.600', 'light.100')(props)
  };
}

export default {
  baseStyle
};
